CREATE TABLE OPENJPA_SEQUENCE_TABLE (ID TINYINT NOT NULL, SEQUENCE_VALUE NUMERIC(38) NULL, UNQ_INDEX NUMERIC IDENTITY UNIQUE, PRIMARY KEY (ID));
CREATE TABLE tempo_acl (id NUMERIC(38) NOT NULL, action VARCHAR(255) NULL, DTYPE VARCHAR(255) NULL, UNQ_INDEX NUMERIC IDENTITY UNIQUE, PRIMARY KEY (id));
CREATE TABLE tempo_acl_map (TASK_ID NUMERIC(38) NULL, ELEMENT_ID NUMERIC(38) NULL, UNQ_INDEX NUMERIC IDENTITY UNIQUE);
CREATE TABLE tempo_attachment (id NUMERIC(38) NOT NULL, payload_url VARCHAR(255) NULL, METADATA_ID NUMERIC(38) NULL, UNQ_INDEX NUMERIC IDENTITY UNIQUE, PRIMARY KEY (id));
CREATE TABLE tempo_attachment_map (PATASK_ID NUMERIC(38) NULL, ELEMENT_ID NUMERIC(38) NULL, UNQ_INDEX NUMERIC IDENTITY UNIQUE);
CREATE TABLE tempo_attachment_meta (id NUMERIC(38) NOT NULL, creation_date DATETIME NULL, description VARCHAR(255) NULL, file_name VARCHAR(255) NULL, mime_type VARCHAR(255) NULL, title VARCHAR(255) NULL, widget VARCHAR(255) NULL, UNQ_INDEX NUMERIC IDENTITY UNIQUE, PRIMARY KEY (id));
CREATE TABLE tempo_notification (id NUMERIC(38) NOT NULL, failure_code VARCHAR(255) NULL, failure_reason VARCHAR(255) NULL, input_xml TEXT NULL, priority INT NULL, state SMALLINT NULL, UNQ_INDEX NUMERIC IDENTITY UNIQUE, PRIMARY KEY (id));
CREATE TABLE tempo_pa (id NUMERIC(38) NOT NULL, complete_soap_action VARCHAR(255) NULL, deadline DATETIME NULL, failure_code VARCHAR(255) NULL, failure_reason VARCHAR(255) NULL, input_xml TEXT NULL, is_chained_before TINYINT NULL, output_xml TEXT NULL, previous_task_id VARCHAR(255) NULL, priority INT NULL, process_id VARCHAR(255) NULL, state SMALLINT NULL, UNQ_INDEX NUMERIC IDENTITY UNIQUE, PRIMARY KEY (id));
CREATE TABLE tempo_pipa (id NUMERIC(38) NOT NULL, init_message VARCHAR(255) NULL, init_soap VARCHAR(255) NULL, process_endpoint VARCHAR(255) NULL, UNQ_INDEX NUMERIC IDENTITY UNIQUE, PRIMARY KEY (id));
CREATE TABLE tempo_role (ACL_ID NUMERIC(38) NULL, element VARCHAR(255) NULL, TASK_ID NUMERIC(38) NULL, UNQ_INDEX NUMERIC IDENTITY UNIQUE);
CREATE TABLE tempo_task (id NUMERIC(38) NOT NULL, creation_date DATETIME NULL, description VARCHAR(255) NULL, form_url VARCHAR(255) NULL, task_idVARCHAR(255) NULL, internal_id INT NULL, UNQ_INDEX NUMERIC IDENTITY UNIQUE, PRIMARY KEY (id));
CREATE TABLE tempo_user (ACL_ID NUMERIC(38) NULL, element VARCHAR(255) NULL, TASK_ID NUMERIC(38) NULL, UNQ_INDEX NUMERIC IDENTITY UNIQUE);
