con:config.script:
/**
 This is a sample groovy test script to access workflow base processes
*/
/* 
  ************************************************************************************************
  ************************************************************************************************
    Workflow Testing Internal Methods. Just copy and paste 
  ************************************************************************************************
  ************************************************************************************************
*/

def getStep(stepName) {
  return testRunner.testCase.getTestStepByName(stepName)
}

def printMethods(obj) {
   def pp = obj.class.methods
   for(m in pp) log.info(m.toString())
}

def requestHolderFromStep(step) {
/*
  a step has the following properties:
  one of Domain, Password, Username, Response, Request, Endpoint
/*
 def properties = step.getPropertyNames()
 for(prop in properties) {log.info(prop)}
*/
   def stepRequest = step.getProperty("Request")
   def requestC = stepRequest.testStep.getTestRequest().getRequestContent()
   def utils = new com.eviware.soapui.support.GroovyUtils( context )
   def holder = utils.getXmlHolder(requestC)  
   return holder
}

def updateRequestWithMap(step, map) {
  def holder = requestHolderFromStep(step)
  map.each {key, value -> holder.setNodeValue(key,value)}
  step.testStep.getTestRequest().setRequestContent(holder.xml)
}

def sendSoapRequest(step, map) {
   currentStep = getStep(step)
   updateRequestWithMap(currentStep, map)
   result = currentStep.run(testRunner, context)
   resultHolder = new com.eviware.soapui.support.GroovyUtils( context ).getXmlHolder(result.responseContentAsXml)
   return resultHolder
}

/**
  Pause time in seconds
*/
def pause(time) {
    log.info("Wating for "+ time +" seconds") 
    synchronized(this) {wait(time*1000)}
}

/* 
  ************************************************************************************************
  ************************************************************************************************
    Starting the workflow test case
  ************************************************************************************************
  ************************************************************************************************
*/

/* 
  Run the authenticate method against token service and get the 
  security token
*/
authenticateResult = sendSoapRequest("Authenticate" , ["tok:user":"admin", "tok:password":"changeit"])
token = authenticateResult.getNodeValue("//tokenws:token")
log.info("Using security token:"+token)

/*
  Get the pipa ID to start 
*/
pipaRequestMap = [ 
  "//tas:taskType": "PIPATask",
  "//tas:participantToken":token,
  "//tas:subQuery": "T._description like '%Examples%'"
]
pipaResultHolder = sendSoapRequest("GetTasks", pipaRequestMap)
pipaId = pipaResultHolder.getNodeValue("//tms:taskId")
log.info("PIPA Id:"+pipaId)

/*
  Init the process related to the pipa
*/
initMap = [
  "//tas:participantToken" : token,
  "//tas:taskId" : pipaId
]
sendSoapRequest("InitFromPipa" , initMap)

/*
   Waiting a few seconds
*/
pause(2)

/*
   Get the new activity task generated by the process
*/
requestMap = [
 "//tas:participantToken":token,
 "//tas:taskType": "PATask",
 "//tas:subQuery" : "T._state = TaskState.READY and T._description like '%Absence%' ORDER BY T._creationDate DESC"
]
taskListResponse = sendSoapRequest("GetTasks", requestMap)
tasks = taskListResponse.getDomNodes("//tms:taskId")
// check we only have 1 activy task to complete
if(tasks.length <= 0 || tasks.length > 1) throw new Exception("Number of tasks is weird")
// getValueAsString(): http://www.docjar.com/docs/api/org/apache/xmlbeans/impl/store/Xobj$ElementXobj.html
activityTaskId = tasks[0].getValueAsString()

/*
   Complete the task
*/
log.info("We are going to complete this task"+activityTaskId)
completeMap = [
   "//ib4p:participantToken":token,
   "//ib4p:taskId": activityTaskId,
   "//ib4p:user" : "admin"
]
completeResult = sendSoapRequest("CompleteTMPTask" , completeMap)
log.info(completeResult.xml)

/* 
   Get and Complete notification
*/
requestMap = [
 "//tas:participantToken":token,
 "//tas:taskType": "Notification",
 "//tas:subQuery" : "T._state = TaskState.READY ORDER BY T._creationDate DESC"
]
taskListResponse = sendSoapRequest("GetTasks", requestMap)
tasks = taskListResponse.getDomNodes("//tms:taskId")

pause(1)

requestMap = [
 "//tas:participantToken":token,
 "//tas:taskId": tasks[0].getValueAsString()
]
completeResult = sendSoapRequest("CompleteTMSTask", requestMap)
log.info(completeResult.xml)

con:transfers.con:targetPath:
